import React, { Component } from 'react';
import API from '../../utils/api'
import Search from ''

class Table extends Component {
    state = {
        employees: [],
    };

    componentDidMount() {
        API.getEmpData()
            .then((res) => {
                this.setState({ employees: res.data.results })
            })
            .catch((err) => console.log(err));
    };

    filterSearch = () => {
        return this.state.employees.filter((employee) => {
            return (employee.name.first.toLowerCase().startsWith(this.props.searchTerm.toLowerCase()) || employee.name.last.toLowerCase().startsWith(this.props.searchTerm.toLowerCase()));
        })
    }



};

export default Table
