import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from "react";

function App() {
  const [empArray, setEmpArray] = useState([]);

  useEffect(() => {
    fetch("https://randomuser.me/api/?results=15")
      .then((response) => response.json())
      .then((data) => {

        setEmployeeArray(data.results);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
  };
  const handleNameSearch = (e) => {

    empArray.filter((each) => {
      return (
        each.name.first.toLowerCase().includes(e.target.value.toLowerCase()) ||
        each.name.last.toLowerCase().includes(e.target.value.toLowerCase())
      );
    });
    setEmpArray(empArray);
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" onChange={handleNameSearch} />
      </form>

      <table style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>Photo</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Cell Phone</th>
          </tr>
        </thead>
        {empArray.map((each, index) => {
          return (
            <tbody key={index}>
              <tr>
                <td>
                  <img src={each.picture.thumbnail} alt={"headshot of employee"} />
                </td>
                <td>{each.name.first}</td>
                <td>{each.name.last}</td>
                <td>{each.email}</td>
                <td>{each.cell}</td>
              </tr>
            </tbody>
          );
        })}
      </table>
    </>
  );
}

export default App;
